// src/components/StudentDashboard.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { db } from '../firebase';
import { 
  collection, 
  query, 
  where, 
  getDocs, 
  doc, 
  getDoc, 
  addDoc, 
  updateDoc,
  serverTimestamp,
  deleteDoc
} from 'firebase/firestore';

function StudentDashboard() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [student, setStudent] = useState(null);
  const [electionStatus, setElectionStatus] = useState({
    active: false,
    endsAt: null
  });
  const [eligiblePositions, setEligiblePositions] = useState([]);
  const [completedVotes, setCompletedVotes] = useState([]);
  const [selectedCandidates, setSelectedCandidates] = useState({});
  const [submittingVote, setSubmittingVote] = useState({});

  // Get email from local storage
  const email = localStorage.getItem('studentEmail');

  // Fetch student data, election status, and eligible positions
  useEffect(() => {
    const fetchData = async () => {
      if (!email) {
        navigate('/');
        return;
      }

      try {
        // Fetch student data
        const studentsRef = collection(db, 'students');
        const q = query(studentsRef, where("email", "==", email));
        const querySnapshot = await getDocs(q);

        if (querySnapshot.empty) {
          navigate('/student/profile');
          return;
        }

        const studentDoc = querySnapshot.docs[0];
        const studentData = {
          id: studentDoc.id,
          ...studentDoc.data()
        };
        setStudent(studentData);

        // Fetch election status
        const settingsDoc = await getDoc(doc(db, 'settings', 'election'));
        if (settingsDoc.exists()) {
          setElectionStatus({
            active: settingsDoc.data().active || false,
            endsAt: settingsDoc.data().endsAt?.toDate() || null
          });
        }

        // Fetch positions eligible for the student
        const positionsRef = collection(db, 'positions');
        const positionsSnapshot = await getDocs(positionsRef);

        const positions = [];
        for (const positionDoc of positionsSnapshot.docs) {
          const positionData = {
            id: positionDoc.id,
            ...positionDoc.data()
          };

          // Check if student is eligible for this position based on year and department
          const isEligibleByYear = positionData.category === studentData.year;
          const isEligibleByDepartment = !positionData.departmentSpecific || 
                                         positionData.department === studentData.department;
          const isGenderEligible = !positionData.gender || 
                                    positionData.gender === 'Any Gender' || 
                                    positionData.gender === studentData.gender ||
                                    positionData.gender === 'Gender Neutral';

          if (isEligibleByYear && isEligibleByDepartment && isGenderEligible) {
            // Fetch candidates for this position
            const candidatesRef = collection(db, 'candidates');
            const candidatesQuery = query(candidatesRef, where("position", "==", positionData.name));
            const candidatesSnapshot = await getDocs(candidatesQuery);

            const candidates = candidatesSnapshot.docs.map(doc => ({
              id: doc.id,
              ...doc.data()
            }));

            positions.push({
              ...positionData,
              candidates
            });
          }
        }

        setEligiblePositions(positions);

        // Fetch completed votes by this student
        const votesRef = collection(db, 'votes');
        const votesQuery = query(votesRef, where("studentId", "==", studentData.id));
        const votesSnapshot = await getDocs(votesQuery);

        const votes = [];
        for (const voteDoc of votesSnapshot.docs) {
          const voteData = voteDoc.data();

          // Get position details
          const positionDoc = await getDoc(doc(db, 'positions', voteData.positionId));

          // Get candidate details
          const candidateDoc = await getDoc(doc(db, 'candidates', voteData.candidateId));

          if (positionDoc.exists() && candidateDoc.exists()) {
            votes.push({
              id: voteDoc.id,
              position: {
                id: positionDoc.id,
                ...positionDoc.data()
              },
              candidate: {
                id: candidateDoc.id,
                ...candidateDoc.data()
              },
              timestamp: voteData.timestamp?.toDate() || null
            });
          }
        }

        setCompletedVotes(votes);

        // Remove already voted positions from eligible positions
        const votedPositionIds = votes.map(vote => vote.position.id);
        const filteredPositions = positions.filter(position => !votedPositionIds.includes(position.id));
        setEligiblePositions(filteredPositions);

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setError('Failed to load dashboard data. Please try again later.');
        setLoading(false);
      }
    };

    fetchData();
  }, [email, navigate]);

  // Handle candidate selection
  const handleCandidateSelect = (positionId, candidateId, maxSelections) => {
    setSelectedCandidates(prev => {
      const currentSelections = prev[positionId] || [];

      // If candidate is already selected, remove it
      if (currentSelections.includes(candidateId)) {
        return {
          ...prev,
          [positionId]: currentSelections.filter(id => id !== candidateId)
        };
      }

      // If selecting a new candidate and we've reached max selections, prevent
      if (currentSelections.length >= maxSelections) {
        return prev;
      }

      // Add new selection
      return {
        ...prev,
        [positionId]: [...currentSelections, candidateId]
      };
    });
  };

  // Submit vote for a position
  const submitVote = async (positionId) => {
    if (!selectedCandidates[positionId] || selectedCandidates[positionId].length === 0) {
      alert('Please select at least one candidate.');
      return;
    }

    setSubmittingVote(prev => ({ ...prev, [positionId]: true }));

    try {
      // Add votes to Firestore
      const votePromises = selectedCandidates[positionId].map(candidateId => 
        addDoc(collection(db, 'votes'), {
          studentId: student.id,
          positionId: positionId,
          candidateId: candidateId,
          timestamp: serverTimestamp()
        })
      );

      await Promise.all(votePromises);

      // Refresh the page to update the lists
      window.location.reload();
    } catch (error) {
      console.error("Error submitting vote:", error);
      alert('Failed to submit vote. Please try again.');
    } finally {
      setSubmittingVote(prev => ({ ...prev, [positionId]: false }));
    }
  };

  // Format remaining time
  const formatTimeRemaining = (endDate) => {
    if (!endDate) return 'No end date set';

    const now = new Date();
    const diff = endDate - now;

    if (diff <= 0) return 'Ended';

    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

    return `${days} days ${hours} hours`;
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('studentEmail');
    navigate('/');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-700">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Header */}
      <header className="bg-blue-900 text-white">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">College Election System</h1>
          <div className="flex items-center space-x-4">
            <span>{student?.name || 'Student'}</span>
            <div className="w-10 h-10 rounded-full bg-gray-300 flex items-center justify-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
            </div>
          </div>
        </div>
      </header>

      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="w-64 bg-white">
          <nav className="mt-10 px-2">
            <Link to="/student/dashboard" className="block py-3 px-4 rounded-md bg-blue-500 text-white mb-1">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h8m-8 6h16" />
                </svg>
                Dashboard
              </div>
            </Link>
            <Link to="/student/profile" className="block py-3 px-4 rounded-md hover:bg-gray-100 text-gray-700 mb-1">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
                My Profile
              </div>
            </Link>
            <Link to="/student/results" className="block py-3 px-4 rounded-md hover:bg-gray-100 text-gray-700 mb-1">
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
                Results
              </div>
            </Link>
            <button 
              onClick={handleLogout}
              className="w-full text-left block py-3 px-4 rounded-md hover:bg-gray-100 text-gray-700"
            >
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                </svg>
                Logout
              </div>
            </button>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="flex-1 p-8">
          <h1 className="text-2xl font-bold text-gray-900 mb-6">Voting Dashboard</h1>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
              {error}
            </div>
          )}

          {/* Election Status */}
          <div className="bg-white rounded-lg shadow p-6 mb-8">
            <div className="flex items-center">
              <h2 className="text-lg font-medium text-gray-900 mr-4">Election Status:</h2>
              <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                electionStatus.active ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
              }`}>
                {electionStatus.active ? 'Active' : 'Inactive'}
              </span>
              {electionStatus.active && electionStatus.endsAt && (
                <span className="ml-4 text-gray-600">
                  Ends in: {formatTimeRemaining(electionStatus.endsAt)}
                </span>
              )}
            </div>
          </div>

          {!electionStatus.active && (
            <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-8">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-yellow-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm text-yellow-700">
                    Elections are currently not active. Please check back later.
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Eligible Positions */}
          {electionStatus.active && (
            <>
              <h2 className="text-xl font-semibold text-gray-800 mb-4">Eligible Positions</h2>

              {eligiblePositions.length === 0 ? (
                <div className="bg-white rounded-lg shadow p-6 mb-8 text-center">
                  <p className="text-gray-600">
                    You have voted for all eligible positions or there are no positions available for you.
                  </p>
                </div>
              ) : (
                eligiblePositions.map(position => (
                  <div key={position.id} className="bg-white rounded-lg shadow mb-6 overflow-hidden">
                    <div className="p-6 border-b border-gray-200">
                      <h3 className="text-lg font-medium text-gray-900">
                        {position.name}
                        {position.category && ` (${position.category}${position.department ? ` - ${position.department}` : ''})`}
                      </h3>
                      <div className="mt-2 flex items-center">
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-200 text-gray-800">
                          {position.vacancies || 1} {position.vacancies > 1 ? 'Vacancies' : 'Vacancy'}
                        </span>
                        {position.gender && position.gender !== 'Any Gender' && (
                          <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                            {position.gender}
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="p-6">
                      <p className="text-sm text-gray-600 mb-4">
                        Select {position.vacancies > 1 ? `up to ${position.vacancies}` : '1'} candidate{position.vacancies > 1 ? 's' : ''}:
                      </p>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                        {position.candidates.length === 0 ? (
                          <p className="text-gray-500 col-span-2">No candidates available for this position.</p>
                        ) : (
                          position.candidates.map(candidate => (
                            <div 
                              key={candidate.id} 
                              className={`p-4 rounded-md ${
                                selectedCandidates[position.id]?.includes(candidate.id) 
                                  ? 'bg-blue-50 border border-blue-300' 
                                  : 'bg-gray-50 border border-gray-200'
                              }`}
                              onClick={() => handleCandidateSelect(
                                position.id, 
                                candidate.id, 
                                position.vacancies || 1
                              )}
                            >
                              <div className="flex items-center">
                                <div className={`w-5 h-5 rounded-full border ${
                                  selectedCandidates[position.id]?.includes(candidate.id)
                                    ? 'border-blue-500 bg-blue-500'
                                    : 'border-gray-300 bg-white'
                                } mr-3 flex items-center justify-center`}>
                                  {selectedCandidates[position.id]?.includes(candidate.id) && (
                                    <svg className="w-3 h-3 text-white" fill="currentColor" viewBox="0 0 20 20">
                                      <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                                    </svg>
                                  )}
                                </div>
                                <div>
                                  <p className="font-medium text-gray-800">{candidate.name}</p>
                                  {candidate.year && (
                                    <p className="text-sm text-gray-500">{candidate.year}</p>
                                  )}
                                </div>
                              </div>
                            </div>
                          ))
                        )}
                      </div>

                      <div className="flex justify-end">
                        <button
                          className={`px-4 py-2 rounded-md text-white font-medium ${
                            submittingVote[position.id]
                              ? 'bg-gray-400 cursor-not-allowed'
                              : 'bg-blue-600 hover:bg-blue-700'
                          }`}
                          onClick={() => submitVote(position.id)}
                          disabled={submittingVote[position.id] || !selectedCandidates[position.id] || selectedCandidates[position.id].length === 0}
                        >
                          {submittingVote[position.id] ? 'Submitting...' : 'Submit Vote'}
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </>
          )}

          {/* Completed Votes */}
          {completedVotes.length > 0 && (
            <div className="mt-8">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">Your Completed Votes</h2>

              {completedVotes.map(vote => (
                <div key={vote.id} className="bg-white rounded-lg shadow mb-4 p-6">
                  <div className="flex justify-between items-center">
                    <div>
                      <h3 className="text-lg font-medium text-gray-900">{vote.position.name}</h3>
                      <p className="text-gray-600">Voted for: <span className="font-medium">{vote.candidate.name}</span></p>
                      {vote.timestamp && (
                        <p className="text-sm text-gray-500 mt-1">
                          Submitted on {vote.timestamp.toLocaleDateString()} at {vote.timestamp.toLocaleTimeString()}
                        </p>
                      )}
                    </div>
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-gray-400 text-white">
                      Submitted
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default StudentDashboard;
